function addItemToListGroup(n,t,i){let u=$(n),f=$(t),r=u.val();if(r!=""){let n=document.createElement("li");n.className="list-group-item";n.innerText=r;let t=document.createElement("input");t.name=i;t.type="hidden";t.value=r;let u=document.createElement("span");u.className="float-right list-icon far fa-trash-alt";u.addEventListener("click",function(){removeItem(this)});n.appendChild(t);n.appendChild(u);f.append(n)}}function addIngredient(){addItemToListGroup("#tbIngredient","#lstIngredients","ingredients")}function addMethodStep(){addItemToListGroup("#tbMethodStep","#lstMethod","method")}function removeItem(n){n.parentNode.remove()}